import logo from './logo.svg';
import './App.css';
import { useEffect, useState } from 'react';

function App() {
  const[counter, setCounter] = useState(0);

  useEffect = setCounter((current) => current + 1);

  const handleIncrement = setCounter((current) => current + 1);

  const handleDecrement = setCounter((current) => current - 1);

  return (
    <div>
      <h1>{counter}</h1>

      <button onClick={handleIncrement}>Increment</button>
      <button onClick={handleDecrement}>Decrement</button>
    </div>
  );
}

export default App;
