import './App.css';
import './App.scss'
import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      breakElem: 5,
      sessionElem: 25
    }

    this.handleBrkDecrement = this.handleBrkDecrement.bind(this);
    this.handleBrkIncrement = this.handleBrkIncrement.bind(this);
    this.handleSessionDecrement = this.handleSessionDecrement.bind(this);
    this.handleSessionIncrement = this.handleSessionIncrement.bind(this);
    this.reset = this.reset.bind(this);
    this.startStop = this.startStop.bind(this);

  }

  handleBrkDecrement() {
    if (this.state.breakElem > 1 && this.state.breakElem <= 60) {
      this.setState({
        breakElem: this.state.breakElem - 1
      })
    }
  }

  handleBrkIncrement() {
    if (this.state.breakElem >= 1 && this.state.breakElem < 60) {
      this.setState({
        breakElem: this.state.breakElem + 1
      })
    }
  }


  handleSessionDecrement() {
    if (this.state.sessionElem > 1 && this.state.sessionElem <= 60) {
      this.setState({
        sessionElem: this.state.sessionElem - 1
      })
    }
  }

  handleSessionIncrement() {
    if (this.state.sessionElem >= 1 && this.state.sessionElem < 60) {
      this.setState({
        sessionElem: this.state.sessionElem + 1
      })
    }
  }

  reset() {
    this.setState({
      breakElem: 5,
      sessionElem: 25
    })
  }

  startStop() {

  }

  render() {
    return (
      <div className='pomodoro-container'>
        <div id='break-div'>

          <h1 id='break-label'>Break Length</h1>

          <h2 id='break-length'>{this.state.breakElem}</h2>

          <button
            type='button'
            id='break-decrement'
            onClick={this.handleBrkDecrement}
          >
            decrement
          </button>
          <button
            type='button'
            id='break-increment'
            onClick={this.handleBrkIncrement}
          >
            increment
          </button>

        </div>

        <div id='session-div'>

          <h1 id='session-label'>Session Length</h1>

          <h2 id='session-length'>{this.state.sessionElem}</h2>

          <button
            type='button'
            id='session-decrement'
            onClick={this.handleSessionDecrement}
          >
            decrement
          </button>
          <button
            type='button'
            id='session-increment'
            onClick={this.handleSessionIncrement}
          >
            increment
          </button>

        </div>

        <div id='session-time-div'>

          <h1 id='timer-label'>
            Session Time
          </h1>

          <h2 id='time-left'>
            {this.state.sessionElem}:00
          </h2>

        </div>

        <div className='btn-div-start-stop-reset'>
          <button
            id='start_stop'
            className='btn-start-stop'
            onClick={this.startStop}
          >
            Start
          </button>
          <button
            id='reset'
            className='btn-start-stop'
            onClick={this.reset}
          >
            Reset
          </button>
        </div>
      </div>
    );
  }
}

export default App;
