import './App.css';
import './App.scss'
import React from 'react';

let isSessionTime = true;
let isBreakAndSessionBtnLocked = false;

class App extends React.Component {
  constructor(props) {
    super(props);


    this.state = {
      breakMin: 5,
      sessionMin: 25,
      minutes: 25,
      seconds: 0
    }


    this.handleBrkDecrement = this.handleBrkDecrement.bind(this);
    this.handleBrkIncrement = this.handleBrkIncrement.bind(this);
    this.handleSessionDecrement = this.handleSessionDecrement.bind(this);
    this.handleSessionIncrement = this.handleSessionIncrement.bind(this);

    this.handleBreakAndSessionIncrementAndDecrement = this.handleBreakAndSessionIncrementAndDecrement.bind(this);

    this.reset = this.reset.bind(this);
    this.startStop = this.startStop.bind(this);

  }

  handleBreakAndSessionIncrementAndDecrement(event) {
    let value = event.target.id;

    console.log(value);
  }


  handleBrkDecrement() {
    if (this.state.breakMin > 1 && this.state.breakMin <= 60) {
      this.setState({
        breakMin: this.state.breakMin - 1
      })
    }
  }

  handleBrkIncrement() {
    if (this.state.breakMin >= 1 && this.state.breakMin < 60) {
      this.setState({
        breakMin: this.state.breakMin + 1
      })
    }
  }


  handleSessionDecrement() {
    if (this.state.minutes > 1 && this.state.minutes <= 60) {
      this.setState({
        minutes: this.state.minutes - 1,
        sessionMin: this.state.sessionMin - 1
      })
    }
  }

  handleSessionIncrement() {
    if (this.state.minutes >= 1 && this.state.minutes < 60) {
      this.setState({
        minutes: this.state.minutes + 1,
        sessionMin: this.state.sessionMin + 1
      })
    }
  }

  reset() {
    isBreakAndSessionBtnLocked = false;

    this.setState({
      breakMin: 5,
      minutes: 25,
      sessionMin: 25
    })
  }

  startStop() {
    isBreakAndSessionBtnLocked = !isBreakAndSessionBtnLocked;

    setInterval(() => {
      //SECONDS
      this.setState({
        seconds: this.state.seconds - 1
      })

      if (this.state.seconds == 0) {
        this.setState({
          seconds: 59,
          minutes: this.state.minutes - 1 

        })
      } 

      if(this.state.minutes == 0 && this.state.seconds == 0) {
        isSessionTime = !isSessionTime;

        if(isSessionTime) {
          this.setState({
            minutes: this.state.sessionMin
          })
        } else {
          this.setState({
            minutes: this.state.breakMin
          })
        }

      }
    }, 1000)
  }

  render() {
    return (
      <div className='pomodoro-container'>
        <div id='break-div'>

          <h1 id='break-label'>Break Length</h1>

          <h2 id='break-length'>{this.state.breakMin}</h2>

          <button
            type='button'
            id='break-decrement'
            onClick={this.handleBreakAndSessionIncrementAndDecrement}
          >
            decrement
          </button>
          <button
            type='button'
            id='break-increment'
            onClick={this.handleBreakAndSessionIncrementAndDecrement}
          >
            increment
          </button>

        </div>

        <div id='session-div'>

          <h1 id='session-label'>Session Length</h1>

          <h2 id='session-length'>{this.state.sessionMin}</h2>

          <button
            type='button'
            id='session-decrement'
            onClick={this.handleBreakAndSessionIncrementAndDecrement}
          >
            decrement
          </button>
          <button
            type='button'
            id='session-increment'
            onClick={this.handleBreakAndSessionIncrementAndDecrement}
          >
            increment
          </button>

        </div>

        <div id='session-time-div'>

          <h1 id='timer-label'>
            Session Time
          </h1>

          {
            (this.state.seconds >= 0 && this.state.seconds < 10)
              ? <h2 id='time-left'>{this.state.minutes}:0{this.state.seconds}</h2>
              : <h2 id='time-left'>{this.state.minutes}:{this.state.seconds}</h2>
          }

        </div>

        <div className='btn-div-start-stop-reset'>
          <button
            id='start_stop'
            className='btn-start-stop'
            onClick={this.startStop}
          >
            Start
          </button>
          <button
            id='reset'
            className='btn-start-stop'
            onClick={this.reset}
          >
            Reset
          </button>
        </div>
      </div>
    );
  }
}

export default App;
